#include <iostream>
#include <vector>
#include <set>

using namespace std;

class Solution {
private:
    long long choose(int n, int k) {
        if (k == 0) { return 1; }
        return (n * choose(n - 1, k - 1)) / k;
    }

public:
    int threeSumMulti(vector<int> &arr, int target) {
        sort(arr.begin(), arr.end());
        vector<int> count(max(*(arr.end() - 1) + 1, target + 1), 0);
        for (int i: arr) {
            count[i]++;
        }
        long long ans = 0;
        tuple<int, int, int> cur(0, 0, target), last = cur;
        while (get<0>(cur) <= get<2>(cur)) {
            while (get<1>(cur) <= get<2>(cur)) {
                if (get<0>(cur) == get<2>(cur)) {
                    ans += (long long) choose(count[get<0>(cur)], 3) % 1000000007;
                } else if (get<0>(cur) == get<1>(cur)) {
                    ans += (long long) choose(count[get<0>(cur)], 2) * (long long) count[get<2>(cur)] % 1000000007;
                } else if (get<1>(cur) == get<2>(cur)) {
                    ans += (long long) count[get<0>(cur)] * (long long) choose(count[get<1>(cur)], 2) % 1000000007;
                } else {
                    ans += (long long) count[get<0>(cur)] * (long long) count[get<1>(cur)] *
                           (long long) count[get<2>(cur)] % 1000000007;
                }
                cout << get<0>(cur) << ", " << get<1>(cur) << ", " << get<2>(cur) << endl;
                get<1>(cur)++;
                get<2>(cur)--;
            }
            cur = last;
            get<0>(cur)++;
            get<1>(cur)++;
            get<2>(cur) -= 2;
            last = cur;
        }
        return (int) (ans % 1000000007);
    }
};

